const settings = $input.first().json;
const apiKey = Array.isArray(settings) ? settings[0]?.youtube_api_key : settings?.youtube_api_key;

if (!apiKey) {
  throw new Error('API key do YouTube n√£o encontrada. Configure em Configura√ß√µes > APIs.');
}

const niche = $env.SEARCH_NICHE || 'dark channel';
const publishedAfter = new Date();
publishedAfter.setDate(publishedAfter.getDate() - 7);

return [{
  json: {
    apiKey,
    niche,
    publishedAfter: publishedAfter.toISOString()
  }
}];

const items = $input.first().json.items || [];
const apiKey = $('Preparar Busca').first().json.apiKey;
const niche = $('Preparar Busca').first().json.niche;

const videos = items.map(item => ({
  videoId: item.id.videoId,
  title: item.snippet.title,
  channelTitle: item.snippet.channelTitle,
  channelId: item.snippet.channelId,
  publishedAt: item.snippet.publishedAt,
  thumbnailUrl: item.snippet.thumbnails?.high?.url || item.snippet.thumbnails?.default?.url,
  niche
}));

return [{ json: { videos, apiKey } }];

const statsItems = $input.first().json.items || [];
const videos = $('Analisar dados de v√≠deo').first().json.videos;
const VIRAL_THRESHOLD = 1000; // views por hora

const statsMap = {};
statsItems.forEach(item => {
  statsMap[item.id] = {
    views: parseInt(item.statistics.viewCount || 0),
    likes: parseInt(item.statistics.likeCount || 0),
    comments: parseInt(item.statistics.commentCount || 0)
  };
});

const viralVideos = videos.map(video => {
  const stats = statsMap[video.videoId] || { views: 0, likes: 0, comments: 0 };
  const publishedAt = new Date(video.publishedAt);
  const hoursSincePublished = (Date.now() - publishedAt.getTime()) / (1000 * 60 * 60);
  const viralScore = Math.round(stats.views / Math.max(hoursSincePublished, 1));

  return {
    ...video,
    ...stats,
    viralScore,
    hoursSincePublished: Math.round(hoursSincePublished)
  };
}).filter(v => v.viralScore >= VIRAL_THRESHOLD);

if (viralVideos.length === 0) {
  return [{ json: { hasResults: false } }];
}

return [{ json: { videos: viralVideos, hasResults: true } }];
