const response = $input.first().json;

if (!response.success || !response.configs || response.configs.length === 0) {
  return [{ json: { hasConfigs: false, message: 'No active configs found' } }];
}

return response.configs.map(config => ({
  json: {
    hasConfigs: true,
    user_id: config.user_id,
    niches: config.niches,
    viral_threshold: config.viral_threshold,
    youtube_api_key: config.youtube_api_key
  }
}));

const item = $input.first().json;
const niches = item.niches;
const youtubeApiKey = item.youtube_api_key;
const userId = item.user_id;
const viralThreshold = item.viral_threshold || 1000;

const publishedAfter = new Date();
publishedAfter.setDate(publishedAfter.getDate() - 7);
const publishedAfterISO = publishedAfter.toISOString();

const searchUrls = niches.map(niche => ({
  niche: niche,
  url: `https://www.googleapis.com/youtube/v3/search?part=snippet&type=video&order=viewCount&maxResults=15&publishedAfter=${publishedAfterISO}&q=${encodeURIComponent(niche)}&key=${youtubeApiKey}`
}));

return searchUrls.map(item => ({
  json: {
    user_id: userId,
    viral_threshold: viralThreshold,
    youtube_api_key: youtubeApiKey,
    niche: item.niche,
    searchUrl: item.url
  }
}));

const item = $input.first();
const prevData = $('Prepare Search URLs').first().json;
const searchResult = item.json;

if (!searchResult.items || searchResult.items.length === 0) {
  return [{ json: { hasVideos: false, user_id: prevData.user_id, niche: prevData.niche } }];
}

const videoIds = searchResult.items.map(item => item.id.videoId).join(',');
const statsUrl = `https://www.googleapis.com/youtube/v3/videos?part=statistics,snippet&id=${videoIds}&key=${prevData.youtube_api_key}`;

return [{
  json: {
    hasVideos: true,
    user_id: prevData.user_id,
    niche: prevData.niche,
    viral_threshold: prevData.viral_threshold,
    videos: searchResult.items,
    statsUrl: statsUrl
  }
}];

const item = $input.first();
const prevData = $('Parse Search Results').first().json;
const statsResult = item.json;

const viralThreshold = prevData.viral_threshold;

const viralVideos = statsResult.items
  .map(video => {
    const views = parseInt(video.statistics.viewCount || '0');
    const likes = parseInt(video.statistics.likeCount || '0');
    const comments = parseInt(video.statistics.commentCount || '0');
    const publishedAt = new Date(video.snippet.publishedAt);
    const hoursAgo = (Date.now() - publishedAt.getTime()) / (1000 * 60 * 60);
    const viewsPerHour = hoursAgo > 0 ? views / hoursAgo : 0;

    return {
      video_id: video.id,
      title: video.snippet.title,
      channel_name: video.snippet.channelTitle,
      channel_url: 'https://www.youtube.com/channel/' + video.snippet.channelId,
      video_url: 'https://www.youtube.com/watch?v=' + video.id,
      thumbnail_url: video.snippet.thumbnails?.high?.url || video.snippet.thumbnails?.default?.url,
      views: views,
      likes: likes,
      comments: comments,
      published_at: video.snippet.publishedAt,
      viral_score: Math.round(viewsPerHour),
      niche: prevData.niche,
      hours_ago: Math.round(hoursAgo)
    };
  })
  .filter(video => video.viral_score >= viralThreshold)
  .sort((a, b) => b.viral_score - a.viral_score)
  .slice(0, 5);

if (viralVideos.length === 0) {
  return [{ json: { hasVirals: false, user_id: prevData.user_id, niche: prevData.niche } }];
}

return [{
  json: {
    hasVirals: true,
    user_id: prevData.user_id,
    niche: prevData.niche,
    viralVideos: viralVideos
  }
}];
